import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import PersonalInfoBar from "../components/PersonalInfoBar";

const FormPage = () => {
  const navigate = useNavigate();

  // Kişisel bilgiler
  const [tc, setTc] = useState("");
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [age, setAge] = useState("");

  // Kan değerleri (örnek)
  const [ast, setAst] = useState("");
  const [alt, setAlt] = useState("");

  const handleSubmit = () => {
    const hastaVerisi = {
      tc,
      name,
      surname,
      age,
      labValues: {
        AST: ast,
        ALT: alt,
      },
    };

    navigate("/result", { state: hastaVerisi });
  };

  return (
    <div>
      <PersonalInfoBar />

      <div style={{ padding: "40px", display: "flex", flexDirection: "column", alignItems: "flex-end", gap: "30px" }}>
        
        {/* Kişisel Bilgiler - Sağda üstte */}
        <div style={{ display: "flex", gap: "20px" }}>
          <Field label="T.C." value={tc} onChange={setTc} />
          <Field label="İsim" value={name} onChange={setName} />
          <Field label="Soyisim" value={surname} onChange={setSurname} />
          <Field label="Yaş" value={age} onChange={setAge} type="number" />
        </div>

        {/* Kan Değerleri */}
        <div style={{ display: "flex", gap: "20px" }}>
          <Field label="AST" value={ast} onChange={setAst} type="number" />
          <Field label="ALT" value={alt} onChange={setAlt} type="number" />
        </div>

        <button
          onClick={handleSubmit}
          style={{
            padding: "12px 24px",
            fontSize: "16px",
            border: "none",
            borderRadius: "6px",
            backgroundColor: "#4CAF50",
            color: "white",
            cursor: "pointer",
          }}
        >
          Tahmin Et
        </button>
      </div>
    </div>
  );
};

// Ortak giriş alanı bileşeni
const Field = ({ label, value, onChange, type = "text" }) => (
  <div style={{ display: "flex", flexDirection: "column" }}>
    <label style={{ marginBottom: "5px", fontWeight: "bold", fontSize: "14px" }}>{label}</label>
    <input
      type={type}
      value={value}
      onChange={(e) => onChange(e.target.value)}
      style={{
        padding: "8px",
        borderRadius: "4px",
        border: "1px solid #ccc",
        fontSize: "14px",
        width: "150px",
      }}
      placeholder={`${label} giriniz`}
    />
  </div>
);

export default FormPage;
