import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const LoginPage = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const users = [
    { username: "erva.ergul", password: "123456" },
    { username: "busra.inan", password: "123456" },
    { username: "ege.kuzu", password: "123456" },
    { username: "kevser.semiz", password: "123456" },
    { username: "helin.ozalkan", password: "123456" },
    { username: "sumeyye.agir", password: "123456" },
    { username: "efe.kesler", password: "123456" },
    { username: "devran.sahin", password: "123456" },
    { username: "cengizhan.karaman", password: "123456" },
    { username: "enes.coban", password: "123456" },
    { username: "kerem.guney", password: "123456" }
  ];

  const handleLogin = () => {
    if (!username || !password) {
      setError("Lütfen kullanıcı adı ve şifreyi boş bırakmayınız.");
      return;
    }

    const foundUser = users.find(
      (user) => user.username === username && user.password === password
    );

    if (foundUser) {
      setError("");
      onLogin();
      navigate("/");
    } else {
      setError("Kullanıcı adı veya şifre yanlış.");
    }
  };

  return (
    <div style={styles.container}>
      <img
        src="/images/karaciger.png"
        alt="Karaciğer Görseli"
        style={styles.sideImage}
      />

      <div style={styles.card}>
        <img src="/images/istun_logo.png" alt="Logo" style={styles.logo} />
        <h3 style={styles.title}>"Bilim, Erken Teşhisle Başlar!"</h3>

        <input
          type="text"
          placeholder="Kullanıcı Adı"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={styles.input}
        />
        <input
          type="password"
          placeholder="Şifre"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={styles.input}
        />

        {error && (
          <div style={styles.errorText}>{error}</div>
        )}

        <button onClick={handleLogin} style={styles.button}>
          Giriş Yap
        </button>

        <small style={styles.footerText}>© 2025 Fibrozis Tahmin Sistemi</small>
      </div>
    </div>
  );
};

const styles = {
  container: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#ffffff",
    padding: "20px"
  },
  sideImage: {
    width: "300px",
    maxWidth: "90%",
    marginBottom: "30px",
  },
  card: {
    width: "100%",
    maxWidth: "400px",
    backgroundColor: "#f0f0f0",
    padding: "40px",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  logo: {
    height: "50px",
    marginBottom: "15px",
  },
  title: {
    marginBottom: "20px",
    color: "#2E7D32",
    fontSize: "24px",
  },
  input: {
    width: "100%",
    padding: "10px",
    marginBottom: "15px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    fontSize: "14px",
  },
  button: {
    padding: "10px 20px",
    width: "100%",
    backgroundColor: "#2E7D32",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    fontWeight: "bold",
    cursor: "pointer",
    fontSize: "16px",
    marginTop: "10px",
  },
  footerText: {
    marginTop: "20px",
    color: "#999",
    fontSize: "12px",
  },
  errorText: {
    color: "red",
    marginBottom: "10px",
    fontWeight: "bold",
    textAlign: "center",
    width: "100%",
  },
};

export default LoginPage;
